void ui_Main_screen_destroy(void)
{
    if(ui_Main) lv_obj_del(ui_Main);

    // NULL screen variables
    ui_Main = NULL;
    ui_lblLamp = NULL;
    ui_chartMorseCode = NULL;
    ui_Container1 = NULL;
    ui_chartRadio = NULL;
    ui_Label1 = NULL;
    ui_Container2 = NULL;
    ui_btnMoveLeft = NULL;
    ui_lblFrequency = NULL;
    ui_btnMoveRight = NULL;
    ui_btnTx = NULL;
    ui_Label2 = NULL;
    ui_imgResult = NULL;
    ui_sldBrightness = NULL;
    ui_wndTransparent = NULL;

}
