void ui_Main_screen_destroy(void)
{
    if(ui_Main) lv_obj_del(ui_Main);

    // NULL screen variables
    ui_Main = NULL;
    ui_Container3 = NULL;
    ui_barLevel = NULL;
    ui_Chart1 = NULL;
    ui_Container4 = NULL;
    ui_Container2 = NULL;
    ui_lblTimer = NULL;
    ui_lblCharger = NULL;
    ui_barCharger = NULL;
    ui_lblOutput = NULL;
    ui_barOutput = NULL;
    ui_conHandleHolder = NULL;
    ui_sldHandle = NULL;
    ui_imgResult = NULL;
    ui_sldBrightness = NULL;

}
